import { useState, useEffect } from "react"
import axios from "axios"

const ShowBuildings = (props) => {

    const [buildings, setBuildings] = useState([])

    const userData = localStorage.getItem('userDetails')
    const userDetails = JSON.parse(userData)
    const id = userDetails._id
    const name = userDetails.name

    useEffect(() => {
        axios.get(`http://localhost:3070/api/buildings/getbyuser/${id}`)
        .then((response) => {
            const buildingsData = response.data
            console.log(buildingsData)
            setBuildings(buildingsData)
        })
        .catch((err) => {
            alert(err.message)
        })
    }, [id])

    const handleDelete = async (id) => {
        try {
            console.log('id', id)
            const delResponse = await axios.delete(`http://localhost:3070/api/buildings/${id}`)
            console.log(delResponse)
            const response = await axios.get('http://localhost:3070/api/buildings')
            setBuildings(response.data)
        } catch(error){
            console.log(error)
            alert(error.message)
        }
    } 

    return(
        <div>
            <h2>Showing {name}'s Buildings</h2>
            {
                buildings.map((building, i) => {
                    return(
                        <div key={building._id}>
                            <h2>Building No: {i+1} </h2>
                            <p>Building Name: {building.name}</p>
                            <p>Address: {building.address}</p>
                            <p>Landmark: {building.landmark}</p>
                            <button onClick={() => handleDelete(building._id)}>Delete Building</button>
                        </div>
                    )
                })
            }
        </div>

    )
}

export default ShowBuildings
