import { useState } from "react"
import axios from "axios"
import { useNavigate, useParams } from "react-router-dom"
import { routes } from "../App"
import ShowRooms from "./ShowRooms"

const AddRoom = () => {
    const [room, setRoom] = useState('')
    const params = useParams()
    console.log('params', params)
    const [showRooms, setShowRooms] = useState(false)
    const navigate =useNavigate()

    const addRoom = async (room) => {
        const buildingData = localStorage.getItem('buildingDetails')
        const building = JSON.parse(buildingData)
        // console.log(building)
        const Id = building._id
        try {
            const response = await axios.post('http://localhost:3070/api/rooms', {
                buildingId: Id,
                name: room
            })
            // console.log(response.data)
            const roomData = JSON.stringify(response.data)
            localStorage.setItem('roomDetails', roomData)
        }catch(error){
            console.log(error.message)
        }
    }

    const submitRoom = async (e) => {
        e.preventDefault()
        if(!room){
            alert('Enter Room details')
        } else {
        await addRoom(room)
        navigate(routes[3].path)
        }
    }

    const handleShowRooms = () => {
        setShowRooms(!showRooms)
    }

    return(
        <div>
            { showRooms && <ShowRooms /> }
            <form onSubmit={submitRoom}>
                <label>Room Name</label>
                <input
                    type='text'
                    placeholder="enter room name"
                    value={room}
                    onChange={(e)=>setRoom(e.target.value)} 
                />
                <input
                    type='submit'
                    value="Save Room" 
                />
            </form>

            <button onClick={() => handleShowRooms()}>Show Rooms</button>
        </div>
    )
}

export default AddRoom
