            rooms.length > 0 ? (
                <div>
                    <h2>Showing {build_Name}'s Rooms</h2>
                    {
                        rooms.map((room, i) => {
                            return(
                                <div key={room._id}>
                                <h3>Room No: {i+1}</h3>
                                <p>Room Name: {room.name}</p>
                                </div>
                            )
                        })
                    }
                </div>
                ) : (
                <h2>No Rooms to Show</h2>
            )
        }
        </div>
    )
}

export default ShowTenants
