import axios from "axios"
import { useState } from "react"
import { useNavigate } from "react-router-dom"
import { paths, routes } from "../App" 
import ShowBuildings from "./ShowBuildings"

const AddBuidling = (props) => {

    const [buildName, setBuildName] = useState('')
    const [address, setAddress] = useState('')
    const [landmark, setLandmark] = useState('')
    const [showBuildings, setShowBuildings] = useState(false)
    const navigate = useNavigate()

    const addBuilding = async (buildName, address, landmark) => {
        try{
            const userData = localStorage.getItem('userDetails')
            console.log('userData', userData)
            const user = JSON.parse(userData)
            const Id = user._id
            console.log(Id, 'Id') 

            const response = await axios.post('http://localhost:3070/api/buildings', {
                userId : Id,
                name: buildName,
                address: address,
                landmark: landmark
            })
            console.log(response.data._id)
            navigate(paths.addBuilding + '/' + response.data._id)
            }catch(error){
            console.log(error.message)
        }
    }

    const submitBuilding = async (e) => {
        e.preventDefault()
        if(!buildName || !address || !landmark){
            alert('Enter Details')
        } else {
        await addBuilding(buildName, address, landmark)
        }
    }

    const handleShowBuildings = () => {
        setShowBuildings(!showBuildings)
    }

    return(
        <div>
            { showBuildings && <ShowBuildings />}
            <form onSubmit={submitBuilding}>
                <label>Name</label>
                <input 
                    type='text'
                    placeholder='Enter Building Name'
                    value={buildName}
                    onChange={(e) => setBuildName(e.target.value)}
                /> <br />
                <label>Address</label>
                <textarea
                    placeholder="Enter Address"
                    value={address}
                    rows="5"
                    cols="50"
                    onChange={(e)=> {setAddress(e.target.value)}}
                ></textarea> <br />
                <label>Landmark</label>
                <input 
                    type='text'
                    placeholder="Enter landmark"
                    value={landmark}
                    onChange={(e)=> setLandmark(e.target.value)}
                /> <br />
                <input
                    type='submit'
                    value="Save Address" 
                />
            </form>
            <button onClick={handleShowBuildings}>Show Buildings</button>
        </div>
    )
}
export default AddBuidling
