import axios from "axios"
import { useState } from "react"

const AddBuidling = (props) => {

    const [buildName, setBuildName] = useState('')
    const [address, setAddress] = useState('')
    const [landmark, setLandmark] = useState('')

    const submitBuilding = (e) => {
        e.preventDefault()
        if(!buildName || !address || !landmark){
            alert('Enter Details')
        }
        buildingDetails(buildName, address, landmark)
    }

    const buildingDetails = async (buildName, address, landmark) => {
        const userData = localStorage.getItem('user')
        console.log('userData', userData)
        const user = JSON.parse(userData)
        const Id = user._id
        console.log(Id, 'Id') 
        try{
            const response = await axios.post('http://localhost:3070/api/buildings', {
                userID : Id,
                name: buildName,
                address: address,
                landmark: landmark
            })
            console.log(response.data)
        }catch(error){
            console.log(error.message)
        }
    }

    return(
        <div>
            <form onSubmit={submitBuilding}>
                <label>Name</label>
                <input 
                    type='text'
                    placeholder='Enter Building Name'
                    value={buildName}
                    onChange={(e) => setBuildName(e.target.value)}
                /> <br />
                <label>Address</label>
                <input
                    type='text'
                    placeholder="Enter Address"
                    value={address}
                    onChange={(e)=> setAddress(e.target.value)}
                /> <br />
                <label>Landmark</label>
                <input 
                    type='text'
                    placeholder="Enter landmark"
                    value={landmark}
                    onChange={(e)=> setLandmark(e.target.value)}
                /> <br />
                <input
                    type='submit'
                    value="Save Address" 
                />
            </form>
        </div>
    )
}
export default AddBuidling
