import { useState } from "react"
import axios from "axios"

const AddTenant = () => {
    const [tname, setTname] = useState('')
    const [temail, setTemail] = useState('')
    const [aadhar, setAadhar] = useState('')
    const [number, setNumber] = useState('')

    const submitTenant = (e) => {
        e.preventDefault()
        if(!tname || !temail || !aadhar || !number){
            alert('Enter tenant details')
        }
        addTenant(tname, temail, aadhar, number)
    }

    const addTenant = async (tname, temail, aadhar, number) => {
        const roomData = localStorage.getItem('roomDetails')
        const room = JSON.parse(roomData)
        console.log(room)
        const Id_room = room._Id
        const buildingData = localStorage.getItem('buildingDetails')
        const building = JSON.parse(buildingData)
        const Id_building = building._id
        try{
            const response = axios.post('http://localhost:3070/api/tenants', {
                name: tname,
                email: temail,
                aadhar_number: aadhar,
                mobile: number,
                roomId: Id_room,
                buildingId: Id_building
            })
            console.log(response.data)
        }catch(error){
            console.log(error.message)
        }
    }

    return (
        <div>
            <form onSubmit={submitTenant}>
                <label>Name</label>
                <input 
                    type='text'
                    placeholder="Enter Name of tenant"
                    value={tname}
                    onChange={(e) => setTname(e.target.value)}
                /> <br />
                <label>Email</label>
                <input 
                    type='text'
                    placeholder="Enter email of tenant"
                    value={temail}
                    onChange={(e) => setTemail(e.target.value)}
                /> <br />
                <label>Aadhar Number</label>
                <input 
                    type='text'
                    placeholder="Enter aadhar of tenant"
                    value={aadhar}
                    onChange={(e) => setAadhar(e.target.value)}
                /> <br />
                <label>Mobile Number</label>
                <input 
                    type='text'
                    placeholder="Enter Number of tenant"
                    value={number}
                    onChange={(e) => setNumber(e.target.value)}
                /> <br />
                <input
                    type='submit'
                    value="Save Tenant" 
                />
            </form>
        </div>
    )
}

export default AddTenant
