import axios from "axios"
import { useState, useEffect } from "react"

const ShowRooms = () => {
    const [rooms, setRooms] = useState([])

    const roomData = localStorage.getItem('roomDetails')
    const roomDetails = JSON.parse(roomData)
    const buildingId = roomDetails.buildingId

    const  buildingData = localStorage.getItem('buildingDetails')
    const buildingDetails = JSON.parse(buildingData)
    const buildingName = buildingDetails.name
    // console.log('buildingId', buildingId, 'buildingName', buildingName)

    useEffect(()=>{
        axios.get(`http://localhost:3070/api/rooms/getbybuilding/${buildingId}`)
        .then((response)=>{
            console.log(response.data)
            setRooms(response.data)
        })
        .catch((error) => {
            console.log(error.message)
            alert(error.message)
        })
    }, [buildingId])

    return(
        <div>
            {
            rooms.lenght > 0 ? (
                <div>
                    <h2>Showing {buildingName}'s Rooms</h2>
                    {
                        rooms.map((room, i) => {
                            return(
                                <div key={room._id}>
                                <h3>Room No: {i+1}</h3>
                                <p>Room Name: {room.name}</p>
                                </div>
                            )
                        })
                    }
                </div>
                ) : (
                <h2>No Rooms to Show</h2>
            )
        }
        </div>
    )
}

export default ShowRooms
